    #[test]
    fn test_format_huge() {
        let fact = Calculation {
            value: 0.into(),
            steps: vec![(1, 0)],
            result: CalculationResult::Exact({
                let mut r = Float::with_val(FLOAT_PRECISION, rug::float::Special::Infinity);
                r.next_down();
                r.to_integer().unwrap()
            }),
        };
        let mut s = String::new();
        fact.format(&mut s, false).unwrap();
        assert_eq!(
            s,
            "The factorial of 0 is roughly 2.098578716467387692404358116884 Ã— 10^323228496 \n\n"
        );
    }
